catalog_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below

    var schema = g_form.getValue('dp_schema');
    var parsed = JSON.parse(schema);
    if (parsed && parsed.schema && parsed.schema.properties && Object.keys(parsed.schema.properties).length) {
        var properties = parsed.schema.properties;
        for (var key in properties) {
            var rgx = new RegExp('[^a-zA-Z0-9]', 'g');
            var name = key.replace(rgx, ' ').trim().replaceAll(' ', '_');
            if (properties[key]["minLength"]) {
                var minLength = g_form.getValue(name);
                if (minLength.length < properties[key]["minLength"]) {
                    alert('Minimum value for ' + (properties[key]["title"] || key) + ' should be ' + properties[key]["minLength"]);

                    return false;
                }
            }
			if (properties[key]["maxLength"]) {
                var maxLength = g_form.getValue(name);
                if (maxLength.length > properties[key]["maxLength"]) {
                    alert('Maximum value for ' + (properties[key]["title"] || key) + ' should be ' + properties[key]["maxLength"]);
                    return false;
                }
            }
            if (properties[key]["minimum"]) {
                var minimum = g_form.getValue(name);
                if (minimum < properties[key]["minimum"]) {
                    alert('Minimum value for ' + (properties[key]["title"] || key) + ' should be ' + properties[key]["minimum"]);

                    return false;
                }
            }
			if (properties[key]["maximum"]) {
                var maximum = g_form.getValue(name);
                if (maximum > properties[key]["maximum"]) {
                    alert('Maximum value for ' + (properties[key]["title"] || key) + ' should be ' + properties[key]["maximum"]);

                    return false;
                }
            }
            if (properties[key]["format"] && properties[key]["format"] == "date-time" && properties[key]["formatMinimum"]) {

                var value = g_form.getValue(name);
				if (new Date(value) < new Date()) {
					alert('Lease Expiration Date: Input date is earlier than the minimum allowed.');

					return false;
				}

            }
        }
    }
}
