catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    var gAjax = new GlideAjax('x_vmw_cloudservice.vRACSClientSciptBridge');
    gAjax.addParam('sysparm_name', 'getEndpoint');
    gAjax.addParam('sysparm_deploymentid', newValue);
    gAjax.getXMLAnswer(callBack);

    function callBack(response) {
        var answer = response;
        var parsed = JSON.parse(answer);
        if (parsed.endpoint) {
            g_form.setValue('endpoint', parsed.endpoint);
            g_form.setDisplay('endpoint', true);
        }
        if (parsed.project) {
            g_form.setValue('project', parsed.project);
            g_form.setDisplay('project', true);
        }
		g_form.clearValue('resource');
    }
}
