sp_widget.client_script:
function($scope,$location) {
	/* widget controller */
	var c = this;
	var deleteArr=[];

	for(var keys in c.data.res_arr){
		c.data.checkedAll = false;
		c.data.res_arr[keys].checked= false;
	}


	$scope.activitySelected = function(link) {
		$location.url(link)
	}

	$scope.$on("getPaginationDataEvent", function (evt, response) {
		c.data.res_arr = response.itemsArray;
		$scope.totalRecords = response.totalRecords;
	});

	$scope.filterSearch = function(passedSearch) {
		var query = '';
		if (passedSearch != '') {
			query = 
				'nameLIKE' + passedSearch + '^NQ' +
				'descriptionLIKE' + passedSearch + '^NQ';

		} 
	//	console.log(query);

		var passingObj={'query':query};
		$scope.$broadcast('setPaginationDataEvent', passingObj)
	}

	$scope.setOrder = function (passedOrder) {
		var passingObj = {};
		passingObj = {orderBy : passedOrder.name, orderByAsc: passedOrder.ascending};
		$scope.$broadcast('setPaginationDataEvent', passingObj);
	}


	$scope.deleteResource = function(){
		deleteArr=[];

		if(c.data.checkedAll === true){
			for (var i = 0; i < c.data.res_arr.length; i++) {
				deleteArr.push(c.data.res_arr[i].resourceId);
			}
		}else{
			for(var key in c.data.res_arr){
				if(c.data.res_arr[key].checked === true){
					deleteArr.push(c.data.res_arr[key].resourceId);
				}
			}
		}
		if(deleteArr.length > 0){
			c.data.deleteArray = deleteArr;
			$('.action-modal, .action-modal-backdrop').show();	
		}else{
	//		console.log("Please select resource for delete ...");
		} 

	}

	$scope.confirmUnshare = function(){
		c.data.action = "delete";
		c.server.update().then(function() {
			$scope.$broadcast('setPaginationDataEvent', {'query':''})
			c.data.action = '';
			c.data.deleteArray = '';
		})
	}

}

