sp_widget.script:
(function() {
	//fetch the support url from system property , updating if triggered
	try {
		var appUtil=new CASAppUtil();
		CASLogger.debug("Inside the CAS_support widget");

		data.client_name = (options.client_name) ? (options.client_name) : "CAS";
		data.title = (options.title) ? (options.title) : "Cloud Aomation Services";

		var supportURL = gs.getProperty(appUtil.configuration.properties.supportURL);
		data.support_url = (options.support_url) ? options.support_url : '';

		if(options.support_url != supportURL){
			gs.setProperty(appUtil.configuration.properties.supportURL,options.support_url);
			CASLogger.debug("Inside the CAS_support widget updating the Support URL");
		}


		data.admin_help_text = (options.admin_help_text) ? (options.admin_help_text) : "Check the error logs and consult the documentation. If issue is not resolved or not mentioned in the document, contact us by visiting at";
		data.non_admin_help_text = (options.non_admin_help_text) ? (options.non_admin_help_text) : "Report to Admin Users";
		data.support_text = (options.support_text) ? (options.support_text) : "vRealize Automation ITSM plugin support website.";


	} catch(e) {
		CASLogger.error("Exception caught in the CAS_Support widget"+e);
	}
})();
