sp_widget.client_script:
function($scope,$location) {
	/* widget controller . */
	var c = this;
//console.log(c.data.projectId);
	$scope.$on("getPaginationDataEvent", function(evt, jsonObj) { 
		$scope.catalogItems = jsonObj.itemsArray;
		$scope.totalRecords = jsonObj.totalRecords;
	});

	$scope.filterSearch = function(passedSearch) {
		var query = 'active=true';
		//no search text.
		if(passedSearch != '' ){
			//admin role - will  see all active cat items
			if (c.data.hasAdminRole=='true') {
				query = 
					'active=true^'+
					'nameLIKE'+passedSearch+
					'^ORshort_descriptionLIKE'+passedSearch+
					'^ORcategory.titleLIKE'+passedSearch;
			} else{
				query='user_criteriaLIKE_vracs^sc_cat_item.sc_catalogs=533fe9dfdb2233006adb48703996191b^user_criteria.groupDYNAMICd6435e965f510100a9ad2572f2b47744^ORuser_criteria.userDYNAMIC90d1921e5f510100a9ad2572f2b477fe'+'^sc_cat_item.nameLIKE'+passedSearch+'^ORsc_cat_item.short_descriptionLIKE'+passedSearch+'^ORsc_cat_item.category.titleLIKE'+passedSearch;
			}	
		}
		else{
			if((c.data.hasAdminRole=='false')){
				query='user_criteriaLIKE_vracs^sc_cat_item.sc_catalogs=533fe9dfdb2233006adb48703996191b^user_criteria.groupDYNAMICd6435e965f510100a9ad2572f2b47744^ORuser_criteria.userDYNAMIC90d1921e5f510100a9ad2572f2b477fe'+'^sc_cat_item.nameLIKE'+passedSearch+'^ORsc_cat_item.short_descriptionLIKE'+passedSearch+'^ORsc_cat_item.category.titleLIKE'+passedSearch;
			}
		}
		var passingObj={'query':query};
		$scope.$broadcast('setPaginationDataEvent', passingObj)
	}

	$scope.redirect=function(action,obj){

				$location.search({
					'id':'vra_catalog_form',
					'showP':'false',
					'org_id':obj.org_id,
					'proj_id':c.data.projectId, //obj.parent_id,
					'cat_id':obj.item_id
				});	
	}
}

