sp_widget.script:
(function() {
	/* populate the 'data' o bje  ct */
	/* e.g., data.table = $sp.getValue('table'); */
	try{
		CASLogger.debug('Inside the widget')
		data.show=false;

		var appUtil=new CASAppUtil();
		var daoUtil=new CASDaoUtil();
		var query = '';
		var paginationOptionSchema ;
		var entitledGroups='';

		data.filterText='';
		data.label= {
			header: "Catalog Items",
			badge: ''
		};
		data.deploymentBaseLink = '/'+gs.getProperty(appUtil.configuration.properties.portalPrefix)+'?id=' + appUtil.configuration.page.deploymentCard;

		//fetching id from url
		data.orgId=($sp.getParameter('org_id'))?$sp.getParameter('org_id'):'NULL';
		data.projectId=($sp.getParameter('proj_id'))?$sp.getParameter('proj_id'):'NULL'

		data.hasAdminRole=(gs.getUser().hasRole('admin'))?'true':'false';
		if(data.orgId=='NULL' && data.projectId=='NULL'){
			query='';
			data.pageId='All';
		}else if(data.orgId!='NULL' && data.projectId=='NULL'){
			query='organisation_name='+data.orgId;
			data.pageId='organization/'+data.orgId;
			data.label.badge = 'All (' + $sp.getParameter('org_name') + ')'; 
			data.badgeLink = '/'+gs.getProperty(appUtil.configuration.properties.portalPrefix)+'?id=' + appUtil.configuration.page.organizations+'&showP=true';
		}else{
			query='organisation_name='+data.orgId+'^category='+data.projectId;
			data.pageId='project/'+data.orgId+'/'+data.projectId;
			data.label.badge = $sp.getParameter('proj_name') + ' (' + $sp.getParameter('org_name') + ')';
			data.badgeLink = '/'+gs.getProperty(appUtil.configuration.properties.portalPrefix)+'?id=' + appUtil.configuration.page.organizations+'&org_id=' + data.orgId+'&org_name=' + $sp.getParameter('org_name')+'&showP=true';
		}
		query += '^active=true';
		if(data.hasAdminRole=='true'){
			paginationOptionSchema = {
				windowSize: 8,
				table: appUtil.configuration.table.catalogItem,
				method: 'fetchCatalogItems',
				query: query,
				hasAdminRole:'true',
				pageId:data.pageId+'',
				infiniteScroll : true
			}
		}else if(data.hasAdminRole=='false'){
			paginationOptionSchema = {
				windowSize: 8,
				table:appUtil.configuration.table.user_criteria_mtom,
				method: 'fetchCatalogItems',
				query: 'user_criteriaLIKE_vracs^sc_cat_item.sc_catalogs=533fe9dfdb2233006adb48703996191b^user_criteria.groupDYNAMICd6435e965f510100a9ad2572f2b47744^ORuser_criteria.userDYNAMIC90d1921e5f510100a9ad2572f2b477fe',
				hasAdminRole:'false',
				pageId : data.pageId+'',
				infiniteScroll : true			
			}	
		}
		data.widgetScript = $sp.getWidget('cas_pagination', paginationOptionSchema);
	}catch(e){
		CASLogger.error("Exception caught inside the CAS_"+e);
	}
})();
