sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	try{
		CASLogger.debug("Inside BR vRAGettingActionSchemaResource for Importing Resource Action Schema");

		this.appUtil=new CASAppUtil();
		var addTopic=new CASDaoUtil();
		var organization =current.machine.project.organization;
		//var casHost=organization.cas_url; //old method
		var endPoint='';
		var midserver='';
		var token='';
		var deploymentId=current.machine.deploymentid;
		var MachineId=current.machine.machine_id;
		var vRAActionid=current.sys_id;
		var MachineActionId='';
		if(current.machine!='')
			{
			MachineActionId=current.action_id;

		}

		if(organization.select_automation_service == 'vRealiseAutomationCloud'){
		token=organization.api_token.getDecryptedValue().toString();
		endPoint=organization.cas_url+this.appUtil.configuration.api.machine+deploymentId+"/resources/"+MachineId+"/actions/"+MachineActionId;
		}

		if(organization.select_automation_service == 'vrealizeAutomationService'){

			endPoint=organization.hostname+this.appUtil.configuration.api.machine+deploymentId+"/resources/"+MachineId+"/actions/"+MachineActionId;
			midserver=organization.mid_server.name;
			token=organization.api_token.getDecryptedValue().toString();
		}
		var Actionresponse = this.appUtil.makeApiCall( endPoint, 'get', '',midserver, "application/json", "application/json", 'Bearer '+token);
		var ActionresponseStatus=Actionresponse.getStatusCode();

		if(ActionresponseStatus=='200')
		{
			var Actionresponsebody = Actionresponse.getBody();
			new addTopic.insert("parseActionSchemaForResource",Actionresponsebody,vRAActionid,organization);

		}
	}
	catch(e)
	{
		CASLogger.error('Exception caught inside BR of vRAGettingActionSchemaResource '+e);
	}

})(current, previous);
