sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// var UserCriteria = new sn_uc.UserCriteria();
	// var attr={'name':current.sys_id+'_vRACS_entitlement','user':current.entitled_users+'','group':current.entitled_groups+''};
	//var UserCriteriaId = UserCriteria.create(attr,true);
	this.appUtil = new CASAppUtil();

	try
	{
		CASLogger.debug('Inside  PerformanceEnhancement.Business Rule:');
		var app_scope=gs.getProperty(this.appUtil.configuration.properties.applicationSysID);

		var user_criteriaGR = new GlideRecord('user_criteria');
		user_criteriaGR.addQuery('nameLIKE'+current.sys_id);
		user_criteriaGR.query();
		var inserted_record_id;

		if(user_criteriaGR.next()){
			var UserCriteria = new sn_uc.UserCriteria(user_criteriaGR.sys_id);
			var attributes={'sys_scope':app_scope,'name':current.sys_id+'_vRACS_entitlement','user':current.entitled_users+'','group':current.entitled_groups+''};
			inserted_record_id=UserCriteria.update(attributes,true);
			/*	user_criteriaGR.name=current.sys_id+'_vRACS_entitlement';
		user_criteriaGR.user = current.entitled_users;
		user_criteriaGR.group=current.entitled_groups;
		inserted_record_id = user_criteriaGR.update();
	*/}
		else{
			var UserCriteria2 = new sn_uc.UserCriteria();

			var attributes2={'sys_scope':app_scope,'name':current.sys_id+'_vRACS_entitlement','user':current.entitled_users+'','group':current.entitled_groups+''};
			inserted_record_id=UserCriteria2.create(attributes2,true);
			/*
		user_criteriaGR.name=current.sys_id+'_vRACS_entitlement';
		user_criteriaGR.user = current.entitled_users;
		user_criteriaGR.group=current.entitled_groups;
		inserted_record_id = user_criteriaGR.insert();
	*/}

		var cat_item_arr=current.select_catalog_items.split(',');
		for(var i=0;i<cat_item_arr.length;i++)
		{
			var user_criteria_mtomGR = new GlideRecord('sc_cat_item_user_criteria_mtom');
			user_criteria_mtomGR.addQuery('sc_cat_item='+cat_item_arr[i]+'^user_criteria='+inserted_record_id);
			//gs.addErrorMessage(user_criteria_mtomGR.getEncodedQuery())
			user_criteria_mtomGR.query();
			if(!user_criteria_mtomGR.next()){
				/*var item = new sn_sc.CatItem(cat_item_arr[i]);
			var uc_arr=inserted_record_id.split(',');
			item.setAttributes({'sys_scope':'0d6a0ec1db11bb407c83712ebf96194d'});
`			user_criteria_mtomGR.sys_scope='0d6a0ec1db11bb407c83712ebf96194d';
			item.availableForUserCriteria("add",uc_arr);
			*/
				user_criteria_mtomGR.sys_scope=app_scope;
				user_criteria_mtomGR.sc_cat_item = cat_item_arr[i];
				user_criteria_mtomGR.user_criteria =inserted_record_id;
				user_criteria_mtomGR.insert();

			}

		}

		var current_cat_items=current.select_catalog_items;
		var previous_cat_items=previous.select_catalog_items;

		if(current_cat_items.localeCompare(previous_cat_items)!=0)
		{
			//gs.addErrorMessage("gujrot");
			var current_cat_item_arr=current.select_catalog_items.split(",");
			var previous_cat_item_arr=previous.select_catalog_items.split(",");
			var removed_sys_id=previous_cat_item_arr.filter(function(i) {return current_cat_item_arr.indexOf(i) < 0;});
			var removed_sys_id_str=removed_sys_id.toString();

			var  user_criteria_mtomGR2= new GlideRecord('sc_cat_item_user_criteria_mtom');
			user_criteria_mtomGR2.addQuery('sc_cat_item.sys_idIN'+removed_sys_id_str+'^user_criteria.nameLIKE_vracs');
			user_criteria_mtomGR2.query();
			while(user_criteria_mtomGR2.next()){

				var item2 = new sn_sc.CatItem(user_criteria_mtomGR2.sc_cat_item);
				var uc_arr2=inserted_record_id.split(',');
				item2.availableForUserCriteria("delete",uc_arr2);

				//user_criteria_mtomGR2.deleteMultiple();
			}
		}	
	}
	catch(e)
	{
		CASLogger.error("Exception caught Inside PerformanceEnhancement.Business Rule   "+e);
	}
})(current, previous);
