sys_script.script:
(function executeRule(current, previous /*null when async*/) 
 {
	var appUtil = new CASAppUtil();	

	try
	{
		CASLogger.debug('Inside  vRACS_Delete_VRA_Data.Business Rule:');

//
// 		var activeEntitle = new GlideRecord(appUtil.configuration.table.entitlement);//entitlement
// 		activeEntitle.addQuery('endpoint',current.sys_id);
// 		activeEntitle.addEncodedQuery('active=true');
// 		activeEntitle.query();
// 		if(activeEntitle.next())
// 		{//

// 			gs.addErrorMessage(' There is an active Entitlements with deployments Present');
// 			current.setAbortAction(true);

// 		}
// 		else{
			//Deletion of associated Entitlements.
			var grEntmt = new GlideRecord(appUtil.configuration.table.entitlement);//entitlement
			grEntmt.addQuery('endpoint', current.sys_id);
			grEntmt.query();
			while(grEntmt.next())
			{
				grEntmt.deleteMultiple();
				//grEntmt.update();
			}

			//Deletion of associated catalog items.
			var grCatlg = new GlideRecord(appUtil.configuration.table.catalogItem);
			grCatlg.addQuery('organisation_name', current.sys_id);
			grCatlg.query();
			while(grCatlg.next())
			{
				grCatlg.deleteMultiple();
			}

			//changing the status of assoviated deployments to retired.
			var grDeploy = new GlideRecord(appUtil.configuration.table.deployments);
			grDeploy.addQuery('u_project.organization', current.sys_id);
			grDeploy.query();
			while(grDeploy.next())
			{
				grDeploy.operational_status = '6';
				grDeploy.update();
			} 


			//changing the status of assoviated machines to retired.
			var grMachine = new GlideRecord(appUtil.configuration.table.machines);
			grMachine.addQuery('project.organization', current.sys_id);
			grMachine.query();
			while(grMachine.next())
			{
				grMachine.operational_status = '6';
				grMachine.update();
			}


			var grActions = new GlideRecord(appUtil.configuration.table.action);//x_vmw_cloudservice_vra_cloud_actions.
			grActions.addQuery('deployment.u_project.organization', current.sys_id);	//grActions.addEncodedQuery('deployment.u_project.organization='+current.sys_id+'^ORmachine.project.organization'+current.sys_id);//'refresh_key='+current.refresh_key+'^ORname='+current.name
			grActions.query();
			while(grActions.next())
			{
				grActions.deleteMultiple();
			}

			var grActionM = new GlideRecord(appUtil.configuration.table.action);
			grActionM.addQuery('machine.project.organization', current.sys_id);
			grActionM.query();
			while(grActionM.next())
			{
				grActionM.deleteMultiple();
			}
			//new action deletion code.


			var sharedGR = new GlideRecord(appUtil.configuration.table.shared_resources);
			sharedGR.query();
			while(sharedGR.next())
			{
				sharedGR.deleteMultiple();


		}}
	catch(e)
	{
		CASLogger.error("Exception caught inside vRACS_Delete_VRA_Data.Business Rule" + e);
	}

})(current, previous);
