sys_script.script:
(function executeRule(current, previous /*null when async*/) {

         var appUtil = new CASAppUtil();

	try
	{
         CASLogger.debug("Inside Request approval notifications:Business Rule");
         var value= gs.getProperty(appUtil.configuration.properties.request_approval);
		// 	 var requester=current.document_id.request;
		if(value=='true')
		{

			gs.error('ayu1');
			var gr = new GlideRecordSecure(appUtil.configuration.table.requests);
			gr.addQuery('sys_id',current.document_id);
			gr.query();
			gs.error('ayu2');
			while(gr.next())
			{
				gs.error('ayu3');
				gs.error('gr.stage'+ ayush);
				if(gr.stage=='Request Approved')
					{
						gs.error('ayu4');
				var requested=gr.request.requested_for;
				gs.eventQueue('x_vmw_cloudservice.request_approved',current,requested);

			}
			} 
			var group= gs.getProperty(appUtil.configuration.properties.group_notifications);
			gs.eventQueue('x_vmw_cloudservice.request_approved',current,group);
		}
	}

	catch(e)
	{
		CASLogger.error("Exception caught inside Request approval notifications : Business Rule "+e);
	}


})(current, previous);
