sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var appUtil = new CASAppUtil();
	try
	{
		CASLogger.debug('Inside vRA Create incident from RITM(cmdb_ci):Business Rule');
		// Add your code here
		var group = gs.getProperty(appUtil.configuration.properties.support_group_new);
		var value = gs.getProperty(appUtil.configuration.properties.vRA_incident_for_failure_catalogitems);

		if(value=='true')
		{
			var inc = new GlideRecordSecure(appUtil.configuration.table.incident);
			inc.initialize();
			inc.caller_id=current.request.requested_for;
			inc.category='inquiry';
			var grp=new GlideRecordSecure(appUtil.configuration.table.groups_table);
			grp.addEncodedQuery('name='+group);
			grp.query();
			while(grp.next())
			{
				var grp_sysid=grp.sys_id;
			}
			inc.assignment_group=grp_sysid;				
			inc.short_description='Requested item '+current.number+' completed with errors.';
			inc.cmdb_ci=current.configuration_item;
			inc.description='Item Number :   ' +current.number + '\nItem Name :   ' + current.cat_item.name+'\nRITM Stage :   '+current.stage.getDisplayValue() +'\nRITM State :   '+current.state.getDisplayValue()+'.\nCheck RITM comments for more details';

			inc.insert();}
	}
	catch(e)
	{
		CASLogger.error("Exception caught Inside vRA Create incident from RITM(cmdb_ci):Business Rule"+e);
	}


})(current, previous);
