sys_script.script:
(function executeRule(current, previous /*null when async.*/) {


	try{

		var appUtil = new CASAppUtil();
		var value = gs.getProperty(appUtil.configuration.properties.vraEndpointEmailNotification);

		if(value == 'true'){

			CASLogger.debug("Inside BR Endpoint Email Notification for Insert & Delete Operation");

			//getting the Created By User's Email ID.
			var CreatedBy = new GlideRecordSecure(appUtil.configuration.table.users_table);
			CreatedBy.addEncodedQuery('user_name='+current.sys_created_by);
			CreatedBy.query();

			////getting the Updates By User's Email ID
			//var UpdatedBy = new GlideRecordSecure(appUtil.configuration.table.users_table);
			//UpdatedBy.addEncodedQuery('user_name='+current.sys_updated_by);
			//UpdatedBy.query();

			if(CreatedBy.next()) var Creator_email = CreatedBy.email;
			//if(UpdatedBy.next()) var Updator_email = UpdatedBy.email;

			if(current.operation() == 'insert'){

				gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_re', current, Creator_email);

			}else if(current.operation() == 'delete'){

				gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_de', current, Creator_email,gs.getUserName());
				//if(Creator_email == Updator_email) gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_de', current, Creator_email);
				//else gs.eventQueue('x_vmw_cloudservice.email_for_endpoint_de', current, Creator_email, Updator_email);

			}
		}


	}catch(e){
		CASLogger.error("Exception caught inside BR: Endpoint Insert & Delete Email Notification "+e);
	}

})(current, previous);
