sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	//script
	var projectId=current.ire_projectid;
	var organization=current.ire_organization;
	var projectCheck=new CASDaoUtil().transformProjectCheck(projectId,organization);
	if(projectCheck)
	{
		gs.info('Inside Business Rule iRE Project Update');
		current.u_project=projectCheck;

	}

	if(current.expires_on!='')
	{
		var expireDate= current.expires_on.toString().replace('Z',".000Z");
		current.expires_on= Date.parse(expireDate).toString();

		var currentDate = new Date(); 
		if (current.expires_on < currentDate.getTime())
			current.expired = true;		
	}



	var removeUnderscore= current.ire_status;

	current.status =removeUnderscore.replace(/_/g, ' ');

	if(current.created_at!='')
	{
		var regexp = new RegExp('.[0-9]+Z');
		var spliteTime = regexp.exec(current.created_at.toString()).toString();
		var milliseconds = spliteTime.substring(1, 4);
		var finalDate = current.created_at.toString().replace(spliteTime,'.'+milliseconds+'Z');

		current.created_at= Date.parse(finalDate).toString();

	}


	var UserEmail= current.ire_created_by;
	var gr = new GlideRecord("sys_user");
	gr.addQuery("email", UserEmail);
	gr.query();
	if (gr.next()) {

		current.vra_owner=gr.sys_id;
	}
	current.servicenow_owner=gs.getUserID();
})(current, previous);
