sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

	var appUtil = new CASAppUtil();

	try
	{
		CASLogger.debug('Inside  vRA Incident for failure of day2 action :Business Rule');
		var groups = gs.getProperty(appUtil.configuration.properties.support_group_new);
		var value = gs.getProperty(appUtil.configuration.properties.vRA_incident_for_failure_day2actions);

		if(value=='true')
		{

			var user= new GlideRecordSecure(appUtil.configuration.table.users_table);
			user.addQuery('user_name',current.sys_created_by);
			user.query();
			if(user.next())
			{

				var id=user.sys_id;

			}

			var inc = new GlideRecordSecure(appUtil.configuration.table.incident);

			inc.initialize();
			inc.caller_id = id;
			inc.category = 'inquiry';
			var grp=new GlideRecordSecure(appUtil.configuration.table.groups_table);
			grp.addEncodedQuery('name='+groups);
			grp.query();
			while(grp.next())
			{
				var grp_sysid=grp.sys_id;
			}
			inc.assignment_group=grp_sysid;			

			inc.description = current.comment;

			if (current.action.machine == "")
			{
				inc.short_description = current.action_name + " " + 'request has been failed for deployment' + " " + current.action.deployment.getDisplayValue();
			} 

			else 
			{
				var gr = new GlideRecordSecure(appUtil.configuration.table.relationship);
				gr.addQuery('child', current.action.machine);
				gr.query();
				if (gr.next()) {
					inc.short_description = current.action_name + " " + 'request has been failed for machine' + " " + current.action.machine.getDisplayValue() + " " + "of deployment" + " " + gr.parent.getDisplayValue();
				}

			}

			if (current.action.machine == "") 
			{
				inc.cmdb_ci = current.action.deployment;
			} 
			else
			{
				inc.cmdb_ci = current.action.machine;
			}
			inc.insert();

		}
	}
	catch(e)
	{
		CASLogger.error("Exception caught Inside vRA Incident for failure of day2 action:Business Rule "+e);
	}


})(current, previous);
