sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var appUtil= new CASAppUtil();

	try
	{
		CASLogger.debug("Inside Send email on Notify when entitle is Update :Business Rule");
		var entitlement= gs.getProperty(appUtil.configuration.properties.vRA_entitlement);

		if(entitlement=='true' && (current.active == true || previous.active == true))
		{
			//getting the Created By User's Email ID.
			var CreatedBy = new GlideRecordSecure(appUtil.configuration.table.users_table);
			CreatedBy.addEncodedQuery('user_name='+current.sys_created_by);
			CreatedBy.addEncodedQuery('active=true');
			CreatedBy.query();

			if(CreatedBy.next()) var Creator_email = CreatedBy.email;

			gs.eventQueue('x_vmw_cloudservice.entitlement_creator_u',current,Creator_email);
		}
	}catch(e)
	{
		CASLogger.error("Exception caught inside Send email on Notify when entitle is Update:Business Rule"+e);
	}


})(current, previous);
