sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	try{
		CASLogger.debug("Inside BR MakeResourceDeploymentRelationship for making Realtion with Deployment ");
		this.appUtil=new CASAppUtil();
		var gr = new GlideRecord(this.appUtil.configuration.table.deployments);
		gr.addEncodedQuery('u_project.organization='+current.project.organization+'^deployment_id='+current.deploymentid);
		gr.query();
		if (gr.next()) {

			var vr = new GlideRecord(this.appUtil.configuration.table.relationship);
			vr.addEncodedQuery("parent="+gr.sys_id+'^type='+gs.getProperty(this.appUtil.configuration.properties.relationship)+'^child='+current.sys_id);

			vr.query();
			if (vr.next()) {
				vr.parent = gr.sys_id;
				vr.type = gs.getProperty(this.appUtil.configuration.properties.relationship);
				vr.child = current.sys_id;
				vr.update();
			}else{
				vr.initialize();
				vr.parent = gr.sys_id;
				vr.type = gs.getProperty(this.appUtil.configuration.properties.relationship);
				vr.child = current.sys_id;
				vr.insert();
			}
			//
			var owner = new GlideRecord(this.appUtil.configuration.table.deployments);
			owner.addQuery('deployment_id='+current.deploymentid);
			owner.query();
			if (owner.next()) {

				current.servicenow_owner=owner.servicenow_owner;
				current.vra_owner=owner.vra_owner;
				current.update();
			}
			gr.update();

		}
		//Relationship between Machine and catalog Item

		var deployment = new GlideRecord('x_vmw_cloudservice_deployments');
		deployment.addQuery('deployment_id',current.deploymentid);
		deployment.query();

		if (deployment.next()) {
			current.deployment_sys = deployment.sys_id;
			if(deployment.u_catalog_item!=''){
				current.u_catalog_item=deployment.u_catalog_item;
			}else{
				var catItem = new GlideRecord(this.appUtil.configuration.table.catalogItem);
				catItem.addQuery("cas_uid",deployment.catalog_id);
				catItem.query();
				if(catItem.next()) {
					current.u_catalog_item = catItem.sys_id;
				}
			}
			current.update();
		}
		/*
		var reqItem = new GlideRecord('sc_req_item');
		reqItem.addQuery('x_vmw_cloudservice_deployment_id',current.deploymentid);
		reqItem.query();
		if(reqItem.next()){
			current.servicenow_owner=reqItem.request.requested_for;
			current.update();}
		*/

	}catch(e)
	{
		CASLogger.error("Exception Caught inside BR MakeResourceDeploymentRelationship  "+e);
	}
})(current, previous);
