sys_script.script:
(function executeRule(current, previous /*null when async*/) {
   var appUtil = new CASAppUtil();
	if (current.entitled_machines) {
		try{
			CASLogger.debug('Inside  deleteCMDBEntitlement.Business Rule: Used for deletion of CMDB resource Entitlements');
			var machineGR = new GlideRecord(appUtil.configuration.table.resources);
			machineGR.addEncodedQuery('sys_idIN'+ current.entitled_machines);
			machineGR.query();
			while(machineGR.next()) {
				machineGR.entitled_users = '';
				machineGR.entitled_groups = '';
				machineGR.update();
			}
		} 
		catch(e) {
			CASLogger.error('Exception caught inside deleteCMDBEntitlement BR : if(current.entitled_machines)'+e);
		}

	}

	if (current.entitled_deployments) {
		try{
			CASLogger.debug('Inside  deleteCMDBEntitlement.Business Rule: Used for deletion of CMDB deployment Entitlements');
			var deploymentGR = new GlideRecord(appUtil.configuration.table.deployments);
			deploymentGR.addEncodedQuery('sys_idIN'+ current.entitled_deployments);
			deploymentGR.query();
			while(deploymentGR.next()) {
				deploymentGR.entitled_users = '';
				deploymentGR.entitled_groups = '';
				deploymentGR.update();
			}
		} 
		catch(e) {
			CASLogger.error('Exception caught inside deleteCMDBEntitlement BR : if(current.entitled_deployments)'+e);
		}

	}

	// Add your code here

})(current, previous);
