sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	try
		{
		CASLogger.debug("Inside getOrganizationDetails-Project:Business Rule"+e);
	//CASLogger.error("Manish");
	var appUtil = new CASAppUtil();
	var endpoint='';
	var OrgName='';
	var OrgId='';
	var midserver=current.organization.mid_server.name;
	var token=current.organization.api_token.getDecryptedValue().toString();
	/*
	CAS=
	https://console.cloud.vmware.com/csp/gateway/am/api/orgs/9c7adf46-ae6f-4258-8a94-804376f3e311
	vRA 8=
	http://cava-n-85-081.eng.vmware.com/csp/gateway/am/api/orgs/d87b600c-8c24-46da-83ac-1b3635bb09f5
 	*/
	var orgId = current.organization_id;
	CASLogger.error("Manish="+midserver);
	//for CAS
	//var vRAhost =appUtil.api.consoleHostname;
	endpoint="https://console.cloud.vmware.com/csp/gateway/am/api/orgs/"+orgId;
	CASLogger.error("Manish CAS endpoint= "+endpoint);
	if(current.organization.select_automation_service == 'vrealizeAutomationService'){
		endpoint="http://cava-n-85-081.eng.vmware.com/csp/gateway/am/api/orgs/"+orgId;
		CASLogger.error("Manish vRA endpoint= "+endpoint);
	}
	var response = appUtil.makeApiCall( endpoint, 'get', '',midserver, "application/json", "application/json", 'Bearer '+token);
	var responseStatus=response.getStatusCode();
	CASLogger.error("manish responseStatus= "+responseStatus);
	if(responseStatus=='200'){
		var responsebody = response.getBody();
		var object = appUtil.parse(responsebody);

		OrgName=object.name;
		OrgId=object.id;
		CASLogger.error("Manish Org Name="+OrgName+" and Org id="+OrgId);
	}
	if(OrgName!='' || OrgId!=''){
		var gr= newGlideRecord('x_vmw_cloudservice_register_cas_organization');
		gr.get(current.organization);
		//CASLogger.error("Manish Org Name="+OrgName+" and Org id="+OrgId);
		if(gr.u_organization_name==''){
			CASLogger.error("Manish inside gr.get()");
			gr.u_organization_name=OrgName;
			gr.u_organization_id=OrgId;
			gr.update();
		}
	}
		}
	catch(e)
		{
			CASLogger.error("Exception caught inside getOrganizationDetails-Project:Business Rule"+e);
		}

})(current, previous);
