sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	if(newValue=="")
		{
			g_form.clearValue("entitled_machines");
		}

	var ga = new GlideAjax('CASChangeScriptEntitlementDeployment');
	ga.addParam('sysparm_name', 'HideMachine');
	ga.addParam('entitledDeploymentsoldValue', oldValue);
	ga.addParam('entitledDeploymentsnewValue', newValue);
	var clientSideMachine= g_form.getValue('entitled_machines');
	ga.addParam('entitledDeploymentsmachineValue',clientSideMachine);
	ga.addParam('sysId', g_form.getUniqueValue());
	ga.getXML(MachineDetails);

	function MachineDetails(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");

		g_form.setValue("entitled_machines",answer);



}
}
