sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }


	//CAS Word is renamed to vRA.
      if(newValue=="")
		{
			g_form.clearValue("select_catalog_items");
			return;
		}



	var ga = new GlideAjax('vRAOnChangeHideItems');
	ga.addParam('sysparm_name', 'updateItems');
	ga.addParam('source_sys_id', newValue);
	ga.addParam('child_sys_id', g_form.getValue('select_catalog_items'));
	ga.addParam('target_tablename','sc_cat_item_category' );
	ga.addParam('target_field', 'sc_category');
	ga.getXML(ProjectDetails);
	//CASLogger.error("sysparm_name","source_sys_id","child_sys_id","target_tablename","target_field");

	function ProjectDetails(response)
	{
		var answer = response.responseXML.documentElement.getAttribute("answer");

		g_form.setValue("select_catalog_items",answer);
	}

}
