sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	try{

		CASLogger.debug("Inside BR MakeDeploymentRelationResource for making relationship with Resource");
		this.appUtil=new CASAppUtil();

		var gr = new GlideRecord(this.appUtil.configuration.table.machines);

		gr.addEncodedQuery('project.organization='+current.u_project.organization+'^deploymentid='+current.deployment_id);
		gr.query();
		if (gr.next()) {

			var vr = new GlideRecord(this.appUtil.configuration.table.relationship);
			vr.addEncodedQuery("parent="+current.sys_id+'^type='+gs.getProperty(this.appUtil.configuration.properties.relationship)+'^child='+gr.sys_id);

			vr.query();
			if (vr.next()) {

				vr.parent = current.sys_id;
				vr.type = gs.getProperty(this.appUtil.configuration.properties.relationship);
				vr.child = gr.sys_id;
				vr.update();
			}else{

				vr.initialize();
				vr.parent = current.sys_id;
				vr.type = gs.getProperty(this.appUtil.configuration.properties.relationship);
				vr.child = gr.sys_id;
				vr.insert();
			}
			gr.update();
		}

		if(current.catalog_id!=''){
			var catItem = new GlideRecord(this.appUtil.configuration.table.catalogItem);
			catItem.addQuery("cas_uid",current.catalog_id);
			catItem.query();
			if(catItem.next()) {
				current.u_catalog_item = catItem.sys_id;
				current.update();
			}
		}

		var reqItem = new GlideRecord('sc_req_item');
		reqItem.addQuery('x_vmw_cloudservice_deployment_id',current.deployment_id);
		reqItem.query();
		if(reqItem.next()){
			current.servicenow_owner=reqItem.request.requested_for;
			current.update();
		}
	}
	catch(e)
	{
		CASLogger.error('Exception caught inside BR  of MakeDeploymentRelationResource '+e);
	}

})(current, previous);
