sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
try{

		CASLogger.debug("Inside EmailScript : Entitlement Email Notification for vRA_deploy_action");
        var deploy_action=current.getDisplayValue('deployment_actions');
	if(current.deployment_actions=="")
		{
			template.print('&nbsp;&nbsp;-');
		}
	else{

		template.print(deploy_action);
	}
	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : vRA_deploy_action "+e);
	}

})(current, template, email, email_action, event);
