sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	try {

        CASLogger.debug("Inside EmailScript : Resource Sharing Email Notification for Delete Operation");

		var appUtil = new CASAppUtil();

		//parse the string into JSON Object
		var resSharingRecord = JSON.parse(event.parm2);

		//get the Updated Time & Date
		var updatedTime = (new GlideDateTime()).toString();

		//convert the dateTime to GMT datetime
		var newCreatedOn = appUtil.getGMTDateTime(resSharingRecord.sys_created_on, 'GMT');
		var newUpdatedOn = appUtil.getGMTDateTime(updatedTime, 'GMT');



        //set the subject of an email notification.
        email.setSubject("vRealize Automation - Deployment Sharing " + resSharingRecord.name + " has been Deleted.");

        template.print(
            "<div style='height: inherit; width: 100%; padding: 4px; text-align: center; background-color: #ffffbf; margin: 10px 0px 20px 0px; font-family: arial; font-weight: 400; font-size: 19px;'><strong>Deployment Sharing Details</strong></div><div style='margin: 10px 0px 10px 0px;font-family: arial; font-size: 14.5px;'><p>Hi "+resSharingRecord.sys_created_by+",</p><p>The following Deployments which was available to you has been deleted.</p><br/></div><table style='font-family: arial; border-collapse: collapse; width: 100%;'><tbody><!--<tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'>Resource Sharing</td></tr>--><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Name</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+resSharingRecord.name+"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Description</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+(resSharingRecord.description == '' ? '-' : resSharingRecord.description)+"</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Deployments</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>"+resSharingRecord.deployments+"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Created By</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+resSharingRecord.sys_created_by+"</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Deleted By</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>"+resSharingRecord.sys_updated_by+"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 2px; font-size: 12px;'>Created On</td><td style='border: 1px solid #ffffff; text-align: left; padding: 2px; font-size: 12px;'><strong>"+newCreatedOn+"</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'>Deleted On</td><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'><strong>"+newUpdatedOn+"</strong></td></tr></tbody></table>"
        );

    } catch (e) {
        CASLogger.error("Exception caught inside EmailScript : Resource Sharing Delete Email Notification " + e);
    }

})(current, template, email, email_action, event);
