sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
										   /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
										   /* Optional GlideRecord */ event) {


	try{

		CASLogger.debug("Inside EmailScript : vRA More Details requested by Approver");
		var comments=current.comments.getJournalEntry(1);
		if(comments=="")
		{
			template.print('&nbsp;&nbsp;-');
		}
		else{

			var newComm=comments.split('(Comments)');

			template.print(newComm[1]);
		}
	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : vRA More Details "+e);
	}

	//template.print(current.comments.getJournalEntry(1));



})(current, template, email, email_action, event);
