sys_script_email.script:
(function runMailScript( /* GlideRecord. */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var appUtil = new CASAppUtil();
    try {
        CASLogger.debug('Inside Display_Deployments_RITM :Notification email script');
        var gr = new GlideRecordSecure(appUtil.configuration.table.deployments);

		var dep = '';
		var state = '';

        gr.addQuery('deployment_id', current.x_vmw_cloudservice_deployment_id);
        gr.query();
        if (gr.next()) {
            dep = gr.name;
        } else {
            dep= '&nbsp &nbsp &nbsp &nbsp &nbsp-';
        }

		if (current.state == '4' || current.state == 4) {
			state = "Your Deployment provisioning request has been Failed.";
		} else if (current.state == '3' || current.state == 3) {
			state = "Your Deployment provisioning request has been Successful";
		}

		template.print(
		"<div style='height: inherit; width: 100%; padding: 4px; text-align: center; background-color: #ffffbf; margin: 10px 0px 20px 0px; font-family: arial; font-weight: 400; font-size: 19px;'><strong>Deployment Request ${number} Details</strong></div><div style='margin: 10px 0px 10px 0px;font-family: arial; font-size: 14.5px;'><p>Hi ${opened_by},</p><p>Your Deployment Request ${number} has been completed with the following details.</p></div><p>&nbsp;</p><table style='font-family: arial; border-collapse: collapse; width: 100%; margin-top: -30px;'><tbody><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Requested Item Number</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>${number}</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Catalog Item Name</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>${cat_item}</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Requested Item State</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>"+ state +"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Deployment Details</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+ dep +"</strong></td></tr></tbody></table><p></p>"
		);

    } catch (e) {
        CASLogger.error('Exception caught inside Display_Deployments_RITM :Notification email script' + e);
    }


})(current, template, email, email_action, event);
