sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here.
	try{

			CASLogger.debug("Inside EmailScript : Resource Sharing Email Notification for Insert Operation");

			var appUtil = new CASAppUtil();

			//convert the dateTime to GMT datetime
			var newCreatedOn = appUtil.getGMTDateTime(current.sys_created_on, 'GMT');

			if(current.users != ''){

				//triggering mails in BCC to the Entitled Users.
				var userRecievers = current.users.toString().split(',');

				var user = new GlideRecordSecure(appUtil.configuration.table.users_table);
				user.addQuery("sys_id", userRecievers);
				user.addQuery("email", "!=", "");    
				user.addQuery("active=true");    
				user.addQuery("user_name", "!=", current.sys_created_by);
				user.addQuery("email", "!=", event.parm1);     
				user.query();

				while (user.next()) {
					email.addAddress("bcc", user.email + '', user.getDisplayValue() + '');
				}

			}

			if(current.groups != ''){

				//triggering mails in BCC to the Entitled Groups
				var groupRecievers = current.groups.toString().split(',');

				var group = new GlideRecordSecure(appUtil.configuration.table.group_users_member);
				group.addQuery("group", groupRecievers);
				group.query();

				var preGroup = '';

				while (group.next()) {
					//email trigger for Group Email ID.
					if(group.group.active == true){
						if(preGroup != group.group.name){
							if (group.group.email != '' && group.group.email != event.parm1){
								email.addAddress("bcc", group.group.email + '', group.group.name + '');
							} 
							preGroup=group.group.name;
						}

						//email trigger for Group's Members Email ID
						if (group.user.email != '' && group.user.email != event.parm1 && group.user.active == true) email.addAddress("bcc", group.user.email + '', group.user.name + '');

					}
				}

			}

			template.print(
				"<div style='height: inherit; width: 100%; padding: 4px; text-align: center; background-color: #ffffbf; margin: 10px 0px 20px 0px; font-family: arial; font-weight: 400; font-size: 19px;'><strong>Deployment Sharing Details</strong></div><div style='margin: 10px 0px 10px 0px;font-family: arial; font-size: 14.5px;'><p>Hi,</p><p>The following Deployments have been shared successfully.</p><br/></div><table style='font-family: arial; border-collapse: collapse; width: 100%;'><tbody><!--<tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'>Resource Sharing</td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Name</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+current.name+"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Description</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+(current.description == '' ? '-' : current.description)+"</strong></td></tr>--><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Deployments</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>"+current.getDisplayValue('deployments')+"</strong></td></tr><!--<tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'>Created By</td><td style='border: 1px solid #ffffff; text-align: left; padding: 5px; font-size: 12px;'><strong>"+current.sys_created_by+"</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'>Created On</td><td style='border: 1px solid #dddddd; text-align: left; padding: 5px; font-size: 12px;'><strong>"+newCreatedOn+"</strong></td></tr><tr style='background-color: #ffffff;'><td style='border: 1px solid #ffffff; text-align: left; padding: 2px; font-size: 12px;'>Updated By</td><td style='border: 1px solid #ffffff; text-align: left; padding: 2px; font-size: 12px;'><strong>"+current.sys_updated_by+"</strong></td></tr><tr style='background-color: #dddddd;'><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'>Updated On</td><td style='border: 1px solid #dddddd; text-align: left; padding: 2px; font-size: 12px;'><strong>"+current.sys_updated_on+"</strong></td></tr>--></tbody></table><!--<p>&nbsp;</p><p>${mail_script:fetch_instance_url}</p><p>&nbsp;</p<p>&nbsp;</p>-->"
			);

	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : Resource Sharing Email Notification for Insert Operation "+e);
	}

})(current, template, email, email_action, event);
