sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {
try{

		CASLogger.debug("Inside EmailScript : Entitlement Email Notification for vRA_machine_actions");
        var machine_actions=current.getDisplayValue('machine_actions');
	if(current.machine_actions=="")
		{
			template.print('&nbsp;&nbsp;-');
		}
	else{

		template.print(machine_actions);
	}
	}catch(e){
		CASLogger.error("Exception caught inside EmailScript : Entitlment vRA_machine_actions Email Notification "+e);
	}

})(current, template, email, email_action, event);
