sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
										   /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
										   /* Optional GlideRecord */ event) {
	try {
		CASLogger.debug("Inside EmailScript : vRA approval requested Notification for Insert Operation");
		var appUtil = new CASAppUtil();

		//convert the dateTime to GMT datetime.
		var newCreatedOn = appUtil.getGMTDateTime(current.sys_created_on, 'GMT');
		template.print(newCreatedOn);
	} catch (e) {
		CASLogger.error("Exception caught inside EmailScript : vRA approval requested Notification for Insert Operation " + e);
	}


})(current, template, email, email_action, event);
