sys_script_include.script:
var vRAAvoidParallelScheduleJobs = Class.create();
vRAAvoidParallelScheduleJobs.prototype = {
	initialize: function() {
	},

	checkQueueEmpty : function(type,organization)
	{
		try
		{
			CASLogger.debug('Inside  checkQueueEmpty');
			var gr = new GlideRecordSecure('x_vmw_cloudservice_cas_queue');	
			var query='';
			if(type == 'Project Import')
			{  

				query = 'topicINStartImportProject,StartedImportProject,ParseProject,ReconcileImportProject^state!=Completed^organization='+organization;



			}
			else if(type == 'Catalog Import')
			{


				query = 'topicINStartImportCatalog,ScheduledImportCatalog,PreImportCatalog,ImportCatalog,ReconcileImportCatalog^state!=Completed^organization='+organization;


			}
			else if(type == 'CMDB Import')
			{


				query = 'topicINStartImportCMDB,IndivImportCMDB,createCMDB,preImportResource,parseResource,createResource,reconcileCMDB,startImportActionForDeployment,parseActionForDeployment,parseActionSchemaForDeployment,startImportActionForResource,parseActionForResource,parseActionSchemaForResource^state!=Completed^organization='+organization;


			}

			gr.addEncodedQuery(query);
			gr.query();
			if(gr.next())
			{	

				return false;
			}
			else
			{

				return true;
			}

		}
		catch(e)
		{
			CASLogger.error('Exception caught inside  checkQueueEmpty'+e);
		}
	},


	type: 'vRAAvoidParallelScheduleJobs'
};
