sys_script_include.script:
var vRABaseUrlCheck = Class.create();
vRABaseUrlCheck.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {





fetchBaseUrl: function() {
    try {
        CASLogger.debug('Inside  vRABaseUrlCheck.fetchBaseUrl: Fetch Base Url.');
        this.appUtil = new CASAppUtil();

        return gs.getProperty(this.appUtil.configuration.properties.BaseUrlCheck);

    } catch (e) {
        CASLogger.error("Exception caught inside vRABaseUrlCheck.fetchBaseUrl" + e);
    }
},


    HostNameRegionCheck: function(current)

 {
     try {

         this.appUtil = new CASAppUtil();
         CASLogger.debug('Inside  vRABaseUrlCheck.HostNameRegionCheck: Base Url Check.');
         var midserver = '';
         var endPoint = '';
         var token = '';
         //var casHost=this.appUtil.configuration.api.hostname;

         if (current.select_automation_service == 'vRealiseAutomationCloud') {

             token = current.api_token.getDecryptedValue().toString();
             endPoint = current.cas_url + this.appUtil.configuration.api.projects + "/?size=20&page=0";

         }
         if (current.select_automation_service == 'vrealizeAutomationService') {

             endPoint = current.hostname + this.appUtil.configuration.api.projects + "/?size=20&page=0";
             midserver = current.mid_server.name;
             token = current.api_token.getDecryptedValue().toString();
         }
         var response = this.appUtil.makeApiCall(endPoint, 'get', '', midserver, "application/json", "application/json", 'Bearer ' + token);

         if (response.haveError()) {

             CASLogger.error('REST call error found inside vRABaseUrlCheck.HostNameRegionCheck:' + response.getErrorMessage());
             return false;

         }
     } catch (e) {
         CASLogger.error("Exception caught inside vRABaseUrlCheck.HostNameRegionCheck" + e);
     }
 },
                    type: 'vRABaseUrlCheck'
                });
