sys_script_include.script:
var CASLogger = Class.create();
CASLogger.prototype = {
    initialize: function() {
    },

	type: 'CASLogger'
};

CASLogger.logHeader = gs.getProperty('x_vmw_cloudservice.CAS.log.header','Cloud-Application');
var logVerbosity = gs.getProperty('x_vmw_cloudservice.CAS.logging.verbosity',1);
CASLogger.logLevel = ((logVerbosity!= '' && !gs.nil(logVerbosity) && logVerbosity != undefined) && (logVerbosity >= 0 && logVerbosity <= 3))  ? logVerbosity : 1;


CASLogger.debug = function(s) {
	if (CASLogger.logLevel >= 3){
		gs.info( 'DEBUG >> '+s,CASLogger.logHeader );		
	}
};

CASLogger.info =  function(s) {
	if (CASLogger.logLevel >= 2){
		gs.info( s,CASLogger.logHeader );
	}
};

CASLogger.warn = function(s) {
	if (CASLogger.logLevel >= 1){
		gs.warn( s,CASLogger.logHeader );
	}
};

CASLogger.error = function(s) {
	if (CASLogger.logLevel >= 0){
		gs.error( s,CASLogger.logHeader );
	}
};
