sys_ui_action.script:
var appUtil = new CASAppUtil();

var sharedR= new GlideAggregate(appUtil.configuration.table.shared_resources);
sharedR.addAggregate('COUNT');
sharedR.query();
sharedR.next();
if(sharedR.getAggregate('COUNT')>0)
{
	var flag=0;
	var gr = new GlideRecord(appUtil.configuration.table.shared_resources);
	gr.query();
	while(gr.next())
	{
		var gr1=new GlideRecord(appUtil.configuration.table.deployments);
		gr1.addEncodedQuery('sys_idIN'+gr.deployments);
		gr1.addQuery('u_project.organization',current.sys_id);
		gr1.query();		
		if(gr1.next())

		{
			{
				gs.addErrorMessage('There are resource sharing records related to this endpoint. Please unshare those records first and then proceed with endpoint deletion');

				flag=0;
				break;
			}
		}
		else
		{
			var grEntmt = new GlideRecord(appUtil.configuration.table.entitlement); //entitlement
			grEntmt.addQuery('endpoint', current.sys_id);
			grEntmt.query();
			if (grEntmt.next()) 
			{
				gs.addErrorMessage('There are manage entitlement records related to this endpoint. Please delete those first and then proceed with endpoint deletion');flag=0;
				break;
			}
			else
			{
				flag=1;
			}
		}
	}

	if(flag==1)
	{
		//gs.info('delete Record');
		current.deleteRecord();
	}
}
else
{
	var grEntmt1 = new GlideRecord(appUtil.configuration.table.entitlement); //entitlement
	grEntmt1.addQuery('endpoint', current.sys_id);
	grEntmt1.query();

	if (grEntmt1.next()) {
		gs.addErrorMessage('There are manage entitlement records related to this endpoint. Please delete those first and then proceed with endpoint deletion');

	} else {
		// current.deleteRecord();
		//action.setRedirectURL(previous);
		//gs.addErrorMessage('delete Record');
		current.deleteRecord();
	}
}
