sys_ui_action.script:
onClick();

function onClick(){
	try{
		CASLogger.debug('Inside  Save and Import.onClick:');
		var appUtil=new CASAppUtil();
		var url=gs.getUrlOnStack();
		var valid_record=false;
		var existingRecord= new CASDaoUtil().RegisterRecordCheck(current);


		if(existingRecord>1)
			{
				gs.error('vRealize Automation endpoint has been registered with same name. ');
				current.update();				
				//valid_record=false;
				action.setReturnURL(current);
			}
		else
			{

				var Validate=new vRARegistervRA();   // UPDATED FROM CAS TO VRA
				var ValidateToken=Validate.generateApiToken(current);

				if(ValidateToken!=false)
				{

					current.api_token= ValidateToken;
					gs.addInfoMessage("vRealize Automation Service updated sucessfully.");
					answer=current.update();
					valid_record=false;
					action.setReturnURL(url);
				}

				else
				{

					gs.addErrorMessage('Invalid credentials. Please re-enter');
					valid_record=true;
					action.setReturnURL(url);
				}

				if(valid_record==true)
					{
						action.setRedirectURL(url);
					}
			}


	}
	catch(e){
		CASLogger.error('Exception caught while saving CAS/vRA. : '+e);

	}
}
