sys_ui_page.client_script:
function call() {
    var response = ("$[jvar_response]" + '');
    if (response == "true") {
        alert('INFO : You are authenticated successfully!');
        redirect();
    } else {
        alert('Error : There is an error while authenticating with VRA cloud. Please try again and if issue persist contact your system administrator');
        redirect();
    }
}

function redirect() {
    window.close();
}
