sysauto_script.script:
(function() {

	var appUtil = new CASAppUtil();
	var call = new CASDaoUtil();


	try {
		//if endpoint exists.
		var endpoint = new GlideRecord('x_vmw_cloudservice_register_cas_organization');
		endpoint.addQuery('active',true); 
		endpoint.query();
		if(endpoint.next())
		{
			var check = call.checkConfigurations();
			if (check != true) {
				gs.setProperty(appUtil.configuration.properties.configurationCheck, false);
				var missingConfiguration = check.split(",");
				for (var i = 0; i < missingConfiguration.length; i++) {
					var string = missingConfiguration[i].toString();
					var formattedString1 = gs.getMessage(string);
					gs.error(formattedString1);
				}
			} 
			else {
				gs.setProperty(appUtil.configuration.properties.configurationCheck, true);
				call.UpdateApiToken();
			}

		}
	}catch (e) {
		CASLogger.error("Exception caught inside vRA Generate API Token   " + e);
	}
})();
