function ifScript() {
    try {
        CASLogger.debug("Inside Workflow script: vRealize Automation Request Workflow.");
        this.appUtil = new CASAppUtil();
        var DeploymentDay2Action = gs.getProperty(this.appUtil.configuration.properties.deploymentCategory);
        var ResourceDay2Action = gs.getProperty(this.appUtil.configuration.properties.machineCategory);

        if (current.cat_item.category == DeploymentDay2Action || current.cat_item.category == ResourceDay2Action) {

            var actionRequest = new vRAActionRequestUtil();
            var actionResult = actionRequest.Day2ActionRequestStatus(current);
            if (actionResult.state == '-1') {
                //do Nothing
            } else {
                current.state = actionResult.state;
                current.comments = actionResult.comments;
                current.update();
            }
        } else {
            var req = new vRACSRequestUtil();
            var result = req.requestDeployment(current);
            if (result.state == '-1') {
                //do Nothing
            } else {
                current.state = result.state;
                current.comments = result.comments;
                current.update();
            }
        }
    } catch (e) {
        CASLogger.error("Exception caught Inside Workflow script: vRealize Automation Request Workflow: " + e);
    }
}

